    private void btnCalculateShippingCostActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateShippingCostActionPerformed
        String weights = txtWeight.getText();
        if (weights.length() > 0) {
            int normal = 0, register = 0, ems = 0;
            int weight = Integer.valueOf(weights);
            if (rdoNormalLetter.isSelected()) {
                closeRadioButton();
                normal = sendNormal(weight);
                if (normal != 0) {
                    txtResult.setText(Integer.toString(normal));
                } else {
                    enterNewInformation();
                }
            } else if (rdoRegisterLetter.isSelected()) {
                closeRadioButton();
                register = sendRegister(weight);
                if (register != 0) {
                    txtResult.setText(Integer.toString(register));
                } else {
                    enterNewInformation();
                }
            } else if (rdoEMSLetter.isSelected()) {
                closeRadioButton();
                ems = sendEms(weight);
                if (ems != 0) {
                    txtResult.setText(Integer.toString(ems));
                } else {
                    enterNewInformation();
                }
            } else {
                JOptionPane.showMessageDialog(rootPane, "กรุณาเลือกประเภทของการส่งจดหมายครับ");
            }
        } else {
            JOptionPane.showMessageDialog(rootPane, "กรุณากรอกน้ำหนักจดหมายด้วยครับ");
        }
    }//GEN-LAST:event_btnCalculateShippingCostActionPerformed

    private void btnClearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnClearActionPerformed
        btnGroup.clearSelection();
        rdoNormalLetter.setEnabled(true);
        rdoRegisterLetter.setEnabled(true);
        rdoEMSLetter.setEnabled(true);
        txtWeight.setText("");
        txtWeight.setEditable(true);
        txtResult.setText("");
    }//GEN-LAST:event_btnClearActionPerformed

    private void btnExitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExitActionPerformed
        int option = JOptionPane.showConfirmDialog(rootPane, "Do you want to Exit ?");
        if (option == JOptionPane.YES_OPTION) {
            System.exit(0);
        }
    }//GEN-LAST:event_btnExitActionPerformed

    private void txtWeightKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtWeightKeyTyped
        char c = evt.getKeyChar();
        if (!((c >= '0') && (c <= '9')
                || (c == java.awt.event.KeyEvent.VK_BACK_SPACE)
                || (c == java.awt.event.KeyEvent.VK_DELETE))) {
            getToolkit().beep();
            evt.consume();
        }
    }//GEN-LAST:event_txtWeightKeyTyped

