    public static int sendNormal(int weight) {
        int normal = 0;
        if (weight < 20) {
            normal = 3;
        } else if (weight < 100) {
            normal = 5;
        } else if (weight < 250) {
            normal = 9;
        } else if (weight < 500) {
            normal = 15;
        } else if (weight < 1000) {
            normal = 25;
        } else if (weight < 2000) {
            normal = 45;
        }
        return normal;
    }

    public static int sendRegister(int weight) {
        if (weight < 2000) {
            return sendNormal(weight) + 13;
        } else {
            return 0;
        }
    }

    public static int sendEms(int weight) {
        int ems = 0;
        if (weight < 20) {
            ems = 27;
        } else if (weight < 100) {
            ems = 32;
        } else if (weight < 250) {
            ems = 37;
        } else if (weight < 500) {
            ems = 47;
        } else if (weight < 1000) {
            ems = 62;
        } else if (weight < 2000) {
            ems = 77;
        }
        return ems;
    }

    public void enterNewInformation() throws HeadlessException {
        JOptionPane.showMessageDialog(rootPane, "กรุณาป้อนน้ำหนักจดหมายใหม่ครับ");
        txtWeight.setText("");
        txtWeight.setEditable(true);
    }

    public void closeRadioButton() {
        rdoNormalLetter.setEnabled(false);
        rdoRegisterLetter.setEnabled(false);
        rdoEMSLetter.setEnabled(false);
        txtWeight.setEditable(false);
    }

